portal.customStyles:
.userAction {
    color: #916ccb;
    font-style: italic;
}

.pr-message-item-text__container img {
  width: unset !important;
  max-width: 100%;
  max-height: 75vh;
}

.pr-chat-container {
  background-color: #151314;
}

.pr-chat-head {
  background-color: transparent !important;
}

.pr-sidebar__title {
  color: #b1235b;
  font-size: 25px;
  margin-top: 12px;
  overflow: visible !important;
}

.pr-navigation {
  background-color: transparent;
}

.pr-conversation__footer {
  background-color: transparent;
}

.pr-input {
  background-color: transparent;
}

.pr-voice-recognition {
  display: none;
}

.pr-input__input {
  background-color: #000000;
  border: solid 2px rgb(128 36 61);
}

.pr-powered-by {
  display: none;
}

.pr-sidebar__image {
  border-radius: 50%; 
}

.pr-chat-head {
  background-image: none;
}

.pr-message-footer__text--bot-name {
  display: none;
}

.pr-message-footer__text {
  display: none;
}

.pr-conversation__messages {
  border: none;
  scrollbar-width: none;
}

.pr-conversation__messages::-webkit-scrollbar {
  display: none;
}

.pr-side-panel__display {
  scrollbar-width: none;
}

.pr-side-panel__display::-webkit-scrollbar {
  display: none;
}

.pr-card__body {
  margin-top: 8px;
}

.pr-panel-text {
  padding: 0;
}

.pr-panel-image-viewer-thumb {
  max-height: 70vh;
}

.pr-slider {
  padding: 0;
}
